DROP TABLE IF EXISTS `sml_article`;
CREATE TABLE `sml_article` (
  `id` int(16) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `uid` int(16) DEFAULT NULL,
  `cid` int(16) NOT NULL COMMENT '文章分类  cate的 id',
  `pic` varchar(100) NOT NULL COMMENT '文章缩略图',
  `title` varchar(60) NOT NULL COMMENT '文章标题',
  `viewnum` int(100) NOT NULL DEFAULT '0' COMMENT '热量',
  `istop` tinyint(255) DEFAULT '0' COMMENT '文章是否置顶 0否  1置顶',
  `isview` tinyint(255) DEFAULT NULL COMMENT '封面显示 0 显示 1隐藏',
  `indeximg` tinyint(255) NOT NULL DEFAULT '1' COMMENT '轮播图设置 0:显示，1:隐藏',
  `isdel` tinyint(255) NOT NULL DEFAULT '0' COMMENT '1:删除，0:未删除',
  `sort` int(10) NOT NULL DEFAULT '0' COMMENT '排序',
  `recode` int(16) DEFAULT NULL COMMENT '文章读取码',
  `type` tinyint(255) DEFAULT NULL COMMENT '主题模板 标志[1,2,3,......]',
  `desc` varchar(500) NOT NULL COMMENT '文章描述',
  `content` text NOT NULL COMMENT '文章内容',
  `createtime` datetime DEFAULT NULL COMMENT '操作时间',
  PRIMARY KEY (`id`),
  KEY `id` (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

