INSERT INTO `sml_article` VALUES ('8', '1002', '11', '7', 'MySQL 速查', '687', '0', '0', '1', '0', '1', '0', '1', 'mysql 快速查询养成', '&lt;h2 style=&quot;border-bottom-color:#cccccc;border-bottom-width:2px;border-bottom-style:solid;padding:0px 4px 0px 0px;margin:0px 0px 10px;text-align:center;&quot;&gt;2016-12-28&amp;nbsp;17:19:28&lt;br/&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size:12px;&quot;&gt;摘要&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size:12px;&quot; class=&quot;ue_t&quot;&gt;：这里可以输入很长很长很长很长很长很长很长很长很差的摘要&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;a&amp;nbsp;class=&amp;quot;left&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;prev&amp;quot;&amp;gt;&amp;lt;&amp;lt;/a&amp;gt;\r\n&amp;lt;a&amp;nbsp;class=&amp;quot;right&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;next&amp;quot;&amp;gt;&amp;gt;&amp;lt;/a&amp;gt;&lt;/pre&gt;&lt;pre class=&quot;brush:plain;toolbar:false;&quot;&gt;&amp;lt;a&amp;nbsp;class=&amp;quot;left&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;prev&amp;quot;&amp;gt;&amp;lt;&amp;lt;/a&amp;gt;\r\n&amp;lt;a&amp;nbsp;class=&amp;quot;right&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;next&amp;quot;&amp;gt;&amp;gt;&amp;lt;/a&amp;gt;\r\n&amp;lt;a&amp;nbsp;class=&amp;quot;left&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;prev&amp;quot;&amp;gt;&amp;lt;&amp;lt;/a&amp;gt;\r\n&amp;lt;a&amp;nbsp;class=&amp;quot;right&amp;nbsp;carousel-control&amp;quot;&amp;nbsp;href=&amp;quot;#carousel_box&amp;quot;data-slide=&amp;quot;next&amp;quot;&amp;gt;&amp;gt;&amp;lt;/a&amp;gt;&lt;/pre&gt;&lt;p style=&quot;line-height:1.5em;&quot;&gt;&lt;/p&gt;&lt;h1 label=&quot;标题居左&quot; style=&quot;font-size: 32px; font-weight: bold; border-bottom: 2px solid rgb(204, 204, 204); padding: 0px 4px 0px 0px; text-align: left; margin: 0px 0px 10px;&quot;&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;无限级分类目录树&lt;/span&gt;&lt;br/&gt;&lt;/h1&gt;&lt;pre class=&quot;brush:pl;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;*&amp;nbsp;desc&amp;nbsp;&amp;nbsp;&amp;nbsp;无限极分类目录树\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$items&amp;nbsp;多为数组\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$id&amp;nbsp;\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$pid\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$son\r\n&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;*/\r\nfunction&amp;nbsp;SMLmenuTree($items,&amp;nbsp;$id&amp;nbsp;=&amp;nbsp;&amp;#39;id&amp;#39;,&amp;nbsp;$pid&amp;nbsp;=&amp;nbsp;&amp;#39;pid&amp;#39;,&amp;nbsp;$son&amp;nbsp;=&amp;nbsp;&amp;#39;child&amp;#39;)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tree&amp;nbsp;=&amp;nbsp;array();&amp;nbsp;//格式化的树\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmpMap&amp;nbsp;=&amp;nbsp;array();&amp;nbsp;&amp;nbsp;//临时扁平数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($items&amp;nbsp;as&amp;nbsp;$item)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmpMap[$item[$id]]&amp;nbsp;=&amp;nbsp;$item;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($items&amp;nbsp;as&amp;nbsp;$item)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(isset($tmpMap[$item[$pid]]))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tmpMap[$item[$pid]][$son][]&amp;nbsp;=&amp;nbsp;&amp;amp;$tmpMap[$item[$id]];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$tree[]&amp;nbsp;=&amp;nbsp;&amp;amp;$tmpMap[$item[$id]];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$tree;\r\n}&lt;/pre&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;line-height:1.5em;&quot;&gt;&lt;strong&gt;标题 2&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;来个列表瞅瞅：&lt;/span&gt;&lt;/p&gt;&lt;ol style=&quot;list-style-type: lower-alpha;&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p class=&quot;ue_t&quot;&gt;列表 1&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p class=&quot;ue_t&quot;&gt;列表 2&lt;/p&gt;&lt;/li&gt;&lt;ol style=&quot;list-style-type: lower-roman;&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p class=&quot;ue_t&quot;&gt;多级列表 1&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p class=&quot;ue_t&quot;&gt;多级列表 2&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;li&gt;&lt;p class=&quot;ue_t&quot;&gt;列表 3&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p style=&quot;line-height:1.5em;&quot;&gt;&lt;strong&gt;标题 3&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;来个文字图文混排的&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;这里可以多行&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;右边是图片&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;&quot;&gt;&lt;span style=&quot;font-size:14px;&quot; class=&quot;ue_t&quot;&gt;绝对没有问题的，不信你也可以试试看&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2016-12-16 12:07:31');
